
public class Dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // Declaring and initialization expression
        int i = 1;
 
        // Do-while loop
        do {
 
            // Body of do-while loop
            // Print statement
            System.out.println("Hello World");
 
            // Update expression
            i++;
        }
        while (i < 6);
    
	}

}
